import React from "react";
import AppBar from "@mui/material/AppBar";
import Box from "@mui/material/Box";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import IconButton from "@mui/material/IconButton";
import MenuIcon from "@mui/icons-material/Menu";
import { createTheme, ThemeProvider, styled } from "@mui/material/styles";
import { green } from "@mui/material/colors";
import { TextField } from "@mui/material";
import { Grid } from "@mui/material";
import CategoryListComponent from "./CategoryListComponent";
import MenuOptionComponent from "./MenuOptionComponent";
import firebase from "firebase/compat/app";
import "firebase/firestore";
import { initializeApp } from "firebase/app";
import { getFirestore } from "firebase/firestore";
import { collection, addDoc } from "firebase/firestore";
import { useState } from 'react'

var menu_category = "";
var menu_name = "";
var menu_options = "";
var menu_price = "";
var menu_cost = "";
var menu_stock = "";

function saveMenu() {

  // TODO: Replace the following with your app's Firebase project configuration
// See: https://support.google.com/firebase/answer/7015592
const firebaseConfig = {
  apiKey: "AIzaSyCrwEG5URyH75MIk-FppV_Ypn0kp_gLXYQ",
  authDomain: "utak-exam.firebaseapp.com",
  projectId: "utak-exam",
  storageBucket: "utak-exam.appspot.com",
  messagingSenderId: "588717404562",
  appId: "1:588717404562:web:266a85c9b14a64f6d2514b",
  measurementId: "G-TLZ6N0XBM3"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);


// Initialize Cloud Firestore and get a reference to the service
const db = getFirestore(app);

  try {
    const docRef = addDoc(collection(db, "menu"), {
      category: menu_category,
      name: menu_name,
      options: menu_options,
      price: menu_price,
      cost: menu_cost,
      stock: menu_stock
    });
    console.log("Document written with ID: ", docRef.id);
  } catch (e) {
    console.error("Error adding document: ", e);
  }
}

function AddMenuComponent() {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [selectedIndex, setSelectedIndex] = React.useState(0);
  const open = Boolean(anchorEl);
  const handleClickListItem = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const theme = createTheme({
    palette: {
      primary: {
        main: green[200],
      },
      secondary: {
        main: green[500],
      },
    },

    components: {
      MuiCard: {
        styleOverrides: {
          root: { margin: 8, maxWidth: 345}
        },
      },
    },
  });

  function ButtonAppBar() {
    return (
      <ThemeProvider theme={theme}>
        <AppBar position="static">
          <Toolbar>
            {/* <IconButton
              size="large"
              edge="start"
              color="inherit"
              aria-label="menu"
              sx={{ mr: 2 }}>
              <MenuIcon />
            </IconButton> */}
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              Menu
            </Typography>
            <Button color="inherit">Edit</Button>
            <Button color="inherit">Delete</Button>
          </Toolbar>
        </AppBar>
      </ThemeProvider>
    );
  }

  const [category, setCategory] = useState('');
  const handleCategoryChange = (event) => {
    setCategory(event.target.value);
    menu_category = event.target.value;
    console.log(menu_category);
  };

  const [name, setName] = useState('');
  const handleNameChange = (event) => {
    setName(event.target.value);
    menu_name = event.target.value;
    console.log(menu_name);
  };

  const [options, setOptions] = useState('');
  const handleOptionsChange = (event) => {
    setOptions(event.target.value);
    menu_options = event.target.value;
    console.log(menu_options);
  };

  const [price, setPrice] = useState('');
  const handlePriceChange = (event) => {
    setPrice(event.target.value);
    menu_price = event.target.value;
    console.log(menu_price);
  };

  const [cost, setCost] = useState('');
  const handleCostChange = (event) => {
    setCost(event.target.value);
    menu_cost = event.target.value;
    console.log(menu_cost);
  };

  const [stock, setStock] = useState('');
  const handleStockChange = (event) => {
    setStock(event.target.value);
    menu_stock = event.target.value;
    console.log(menu_stock);
  };

  return (
    <>
        <ButtonAppBar></ButtonAppBar>

        {/* <Grid container rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3 }}>
          <Grid item xs={1}>
            <Button variant="contained" style={{maxWidth: '100px', maxHeight: '40px', minWidth: '100px', minHeight: '40px', margin:'10px'}}>Add</Button>
          </Grid>
          <Grid item xs={1}>
            <Button variant="contained" style={{maxWidth: '100px', maxHeight: '40px', minWidth: '100px', minHeight: '40px', margin:'10px'}}>Edit</Button>
          </Grid>
        </Grid> */}

        <h1 style = {{ margin:'10px' }}>Add Menu Item</h1>
        <Grid container spacing={3}>
        <Grid item xs={8}>
          <TextField id="outlined-basic" label="Category" variant="outlined" onChange={handleCategoryChange} 
          style={{maxWidth: '400px', maxHeight: '40px', minWidth: '400px', minHeight: '40px', margin:'10px'}}/>
        </Grid>
        <Grid item xs={8}>
          <TextField id="outlined-basic" label="Name" variant="outlined" onChange={handleNameChange}
          style={{maxWidth: '400px', maxHeight: '40px', minWidth: '400px', minHeight: '40px', margin:'10px'}}/>
        </Grid>
        <Grid item xs={8}>
        <TextField id="outlined-basic" label="Options" variant="outlined" onChange={handleOptionsChange}
        style={{maxWidth: '400px', maxHeight: '40px', minWidth: '400px', minHeight: '40px', margin:'10px'}}/>
      </Grid>
      <Grid item xs={8}>
          <TextField id="outlined-basic" label="Price" variant="outlined" onChange={handlePriceChange}
          style={{maxWidth: '400px', maxHeight: '40px', minWidth: '400px', minHeight: '40px', margin:'10px'}}/>
      </Grid>
      <Grid item xs={8}>
        <TextField id="outlined-basic" label="Cost" variant="outlined" onChange={handleCostChange}
        style={{maxWidth: '400px', maxHeight: '40px', minWidth: '400px', minHeight: '40px', margin:'10px'}}/>
      </Grid>
      <Grid item xs={8}>
        <TextField id="outlined-basic" label="Stock" variant="outlined" onChange={handleStockChange}
        style={{maxWidth: '400px', maxHeight: '40px', minWidth: '400px', minHeight: '40px', margin:'10px'}}/>
      </Grid>
      <Grid item xs={8}>
        <Button variant="contained" onClick={saveMenu}
        style={{maxWidth: '190px', maxHeight: '40px', minWidth: '190px', minHeight: '40px', margin:'10px'}}
        >Save</Button>
        <Button variant="contained" style={{maxWidth: '190px', maxHeight: '40px', minWidth: '190px', minHeight: '40px', margin:'10px'}}>
          Reset</Button>
      </Grid>
    </Grid>
    </>
  );
}

export default AddMenuComponent;
