import React from "react";
import Button from "@mui/material/Button";
import { TextField } from "@mui/material";
import { Grid } from "@mui/material";
import AppNavBarComponent from "./AppNavBarComponent";
import "firebase/firestore";
import { initializeApp } from "firebase/app";
import { getFirestore } from "firebase/firestore";
import { collection, addDoc } from "firebase/firestore";
import { useState } from 'react'

var menu_category = "";
var menu_name = "";
var menu_options = "";
var menu_price = "";
var menu_cost = "";
var menu_stock = "";

var isEditing = false;

function saveMenu() {

  const firebaseConfig = {
    apiKey: "AIzaSyCrwEG5URyH75MIk-FppV_Ypn0kp_gLXYQ",
    authDomain: "utak-exam.firebaseapp.com",
    projectId: "utak-exam",
    storageBucket: "utak-exam.appspot.com",
    messagingSenderId: "588717404562",
    appId: "1:588717404562:web:266a85c9b14a64f6d2514b",
    measurementId: "G-TLZ6N0XBM3"
  };

  const app = initializeApp(firebaseConfig);
  const db = getFirestore(app);

  try {
    const docRef = addDoc(collection(db, "menu"), {
      category: menu_category,
      name: menu_name,
      options: menu_options,
      price: menu_price,
      cost: menu_cost,
      stock: menu_stock
    });
    console.log("Document written with ID: ", docRef.id);
  } catch (e) {
    console.error("Error adding document: ", e);
  }
}

function AddMenuComponent() {

  const [category, setCategory] = useState('');
  const handleCategoryChange = (event) => {
    setCategory(event.target.value);
    menu_category = event.target.value;
    console.log(menu_category);
  };

  const [name, setName] = useState('');
  const handleNameChange = (event) => {
    setName(event.target.value);
    menu_name = event.target.value;
    console.log(menu_name);
  };

  const [options, setOptions] = useState('');
  const handleOptionsChange = (event) => {
    setOptions(event.target.value);
    menu_options = event.target.value;
    console.log(menu_options);
  };

  const [price, setPrice] = useState('');
  const handlePriceChange = (event) => {
    setPrice(event.target.value);
    menu_price = event.target.value;
    console.log(menu_price);
  };

  const [cost, setCost] = useState('');
  const handleCostChange = (event) => {
    setCost(event.target.value);
    menu_cost = event.target.value;
    console.log(menu_cost);
  };

  const [stock, setStock] = useState('');
  const handleStockChange = (event) => {
    setStock(event.target.value);
    menu_stock = event.target.value;
    console.log(menu_stock);
  };

  const [isEditing, setIsEditing] = useState('');
  const handleisEditingChange = (event) => {
    if (isEditing) {
      setIsEditing(false);
    } else {
      setIsEditing(true);
    }

    isEditing = isEditing;
    console.log(isEditing);
  };

  return (
    <>
        {isEditing && <AppNavBarComponent />}

        <h1 style = {{ margin:'10px' }}>Add Menu Item</h1>
        <Grid container spacing={3}>
        <Grid item xs={8}>
          <TextField className="outlined-basic" label="Category" variant="outlined" onChange={handleCategoryChange}
          style={{maxWidth: '400px', maxHeight: '40px', minWidth: '400px', minHeight: '40px', margin:'10px'}}/>
        </Grid>
        <Grid item xs={8}>
          <TextField id="outlined-basic" label="Name" variant="outlined" onChange={handleNameChange}
          style={{maxWidth: '400px', maxHeight: '40px', minWidth: '400px', minHeight: '40px', margin:'10px'}}/>
        </Grid>
        <Grid item xs={8}>
          <TextField id="outlined-basic" label="Options" variant="outlined" onChange={handleOptionsChange}
          style={{maxWidth: '400px', maxHeight: '40px', minWidth: '400px', minHeight: '40px', margin:'10px'}}/>
        </Grid>
        <Grid item xs={8}>
          <TextField id="outlined-basic" label="Price" variant="outlined" onChange={handlePriceChange}
          style={{maxWidth: '400px', maxHeight: '40px', minWidth: '400px', minHeight: '40px', margin:'10px'}}/>
        </Grid>
        <Grid item xs={8}>
          <TextField id="outlined-basic" label="Cost" variant="outlined" onChange={handleCostChange}
          style={{maxWidth: '400px', maxHeight: '40px', minWidth: '400px', minHeight: '40px', margin:'10px'}}/>
        </Grid>
        <Grid item xs={8}>
          <TextField id="outlined-basic" label="Stock" variant="outlined" onChange={handleStockChange}
          style={{maxWidth: '400px', maxHeight: '40px', minWidth: '400px', minHeight: '40px', margin:'10px'}}/>
        </Grid>
        <Grid item xs={8}>
          <Button variant="contained" onClick={saveMenu}
          style={{maxWidth: '190px', maxHeight: '40px', minWidth: '190px', minHeight: '40px', margin:'10px'}}
          >Save</Button>
        <Button variant="contained" onClick={handleisEditingChange}
          style={{maxWidth: '190px', maxHeight: '40px', minWidth: '190px', minHeight: '40px', margin:'10px'}}>
          Reset</Button>
      </Grid>
    </Grid>
    </>
  );
}

export default AddMenuComponent;
